// Extract headers and body for HMAC validation
const headers = $json.headers || {};
const body = $json.body || $json;
const rawBody = $json.rawBody || JSON.stringify(body);

const hmacSignature = headers['x-signature-256'] || headers['X-Signature-256'] || headers['x-hmac-signature'] || headers['X-HMAC-Signature'];
const timestamp = headers['x-timestamp'] || headers['X-Timestamp'];
const nonce = headers['x-nonce'] || headers['X-Nonce'];

// Validation errors
if (!hmacSignature) {
  return {
    json: {
      isError: true,
      error: 'UNAUTHORIZED',
      message: 'Missing X-Signature-256 header',
      statusCode: 401,
      body,
      headers
    }
  };
}

if (!timestamp) {
  return {
    json: {
      isError: true,
      error: 'BAD_REQUEST',
      message: 'Missing X-Timestamp header',
      statusCode: 400,
      body,
      headers
    }
  };
}

if (!nonce) {
  return {
    json: {
      isError: true,
      error: 'BAD_REQUEST',
      message: 'Missing X-Nonce header (required for replay protection)',
      statusCode: 400,
      body,
      headers
    }
  };
}

const receivedHmac = hmacSignature.replace('sha256=', '').toLowerCase();

return {
  json: {
    isError: false,
    body,
    headers,
    rawBody,
    receivedHmac,
    timestamp: parseInt(timestamp),
    nonce
  }
};

const crypto = require('crypto');

// Get data from current node (from Debug HMAC_SECRETS or Extract Headers & Body)
let rawBody = $json.rawBody || '';
let headers = $json.headers || {};
let receivedHmac = $json.receivedHmac || '';

// Fallback: Try to get data from Extract Headers & Body node directly
if (!receivedHmac || !rawBody) {
  const extractNode = $node['Extract Headers & Body'];
  if (extractNode && extractNode.json) {
    const extractData = Array.isArray(extractNode.json) ? extractNode.json[0] : extractNode.json;
    rawBody = rawBody || extractData.rawBody || '';
    headers = headers || extractData.headers || {};
    receivedHmac = receivedHmac || extractData.receivedHmac || '';
  }
}

// Use receivedHmac from Extract Headers & Body node if available
// Otherwise try to extract from headers as fallback
let expectedSignature = receivedHmac || '';

// Fallback: Get expected signature from header if receivedHmac not available
if (!expectedSignature) {
  const signatureHeader = headers['x-signature-256'] || 
                         headers['X-Signature-256'] || 
                         headers['x-signature'] || 
                         headers['X-Signature'] || 
                         '';
  // Remove 'sha256=' prefix if present
  expectedSignature = signatureHeader.replace('sha256=', '').toLowerCase();
} else {
  // Ensure receivedHmac is already lowercase (from Extract Headers & Body)
  expectedSignature = expectedSignature.toLowerCase();
}

// Get secret from environment variable (set by Debug HMAC_SECRETS node)
const secret = $json.hmac_secret || $env.HMAC_SECRET;

if (!secret) {
  throw new Error('HMAC_SECRET environment variable not set');
}

// Calculate HMAC-SHA256
const hmac = crypto
  .createHmac('sha256', secret)
  .update(rawBody, 'utf8')
  .digest('hex');

const calculatedSignature = hmac.toLowerCase();

// Return result with clear flags
return {
  json: {
    ...$json,
    rawBody: rawBody,
    headers: headers,
    calculatedSignature: calculatedSignature,
    expectedSignature: expectedSignature,
    hmacValid: (!!expectedSignature && expectedSignature !== '' && expectedSignature !== 'dummy' && calculatedSignature === expectedSignature),
    // Keep hash for backward compatibility
    hash: calculatedSignature
  }
};

const timestamp = $json.timestamp;
const now = Math.floor(Date.now() / 1000);
const diffMinutes = Math.abs(now - timestamp) / 60;

if (diffMinutes > 5) {
  return {
    json: {
      isError: true,
      error: 'UNAUTHORIZED',
      message: `Request timestamp too old or in future (±5 minutes required, got ${diffMinutes.toFixed(1)} min diff)`,
      statusCode: 401,
      body: $json.body,
      headers: $json.headers
    }
  };
}

return {
  json: {
    ...$json,
    isError: false,
    timestampValid: true
  }
};

// Get body and binary data - try from current input first, then from Extract Headers & Body node
let body = $json.body;
let binaryData = $binary || {};

// If body is not available in current input, get it from Extract Headers & Body node
if (!body) {
  const extractNode = $node["Extract Headers & Body"];
  if (extractNode && extractNode.json) {
    const extractData = Array.isArray(extractNode.json) ? extractNode.json[0] : extractNode.json;
    body = extractData.body || {};
  }
}

// Ensure body is at least an empty object
if (!body) {
  body = {};
}

if (!body.meeting) {
  return {
    json: {
      isError: true,
      error: 'VALIDATION_ERROR',
      message: 'Missing meeting object in request body',
      statusCode: 400,
      body
    }
  };
}

const meeting = body.meeting;
if (!meeting.slug) {
  return {
    json: {
      isError: true,
      error: 'VALIDATION_ERROR',
      message: 'Missing meeting.slug',
      statusCode: 400,
      body
    }
  };
}

if (!meeting.title || !meeting.datetime) {
  return {
    json: {
      isError: true,
      error: 'VALIDATION_ERROR',
      message: 'Missing meeting.title or meeting.datetime',
      statusCode: 400,
      body
    }
  };
}

let sourceKey = null;
if (binaryData.audio) sourceKey = 'audio';
else if (binaryData.video) sourceKey = 'video';
else if (binaryData.file) sourceKey = 'file';

let hasFile = false;
if (sourceKey) {
  binaryData.audio = binaryData[sourceKey];
  hasFile = true;
}

const audioUrl = body.audioUrl || null;
const filePath = body.filePath || null; // WebDAV path from AUTO_TRIGGER

// Accept either binary file, audioUrl (public URL), or filePath (WebDAV path)
if (!hasFile && !audioUrl && !filePath) {
  return {
    json: {
      isError: true,
      error: 'VALIDATION_ERROR',
      message: 'No audio/video file provided (binary, audioUrl, or filePath)',
      statusCode: 400,
      body
    }
  };
}

let fileExt = 'mp3';
if (hasFile && binaryData.audio) {
  fileExt = binaryData.audio.fileExtension || binaryData.audio.mimeType?.split('/')[1] || 'mp3';
} else if (filePath) {
  // Extract extension from filePath
  const pathParts = filePath.split('.');
  if (pathParts.length > 1) {
    fileExt = pathParts.pop();
  }
}

const basePath = `/Meetings/${meeting.slug}`;
const paths = {
  root: `${basePath}/`,
  raw: `${basePath}/raw/`,
  out: `${basePath}/out/`,
  dlq: `${basePath}/dlq/`,
  originalFile: `${basePath}/raw/original.${fileExt}`,
  transcript: `${basePath}/out/transcript.txt`,
  meta: `${basePath}/out/meta.json`
};

const meta = {
  meeting: {
    slug: meeting.slug,
    title: meeting.title,
    datetime: meeting.datetime,
    language: meeting.language || 'de',
    participants: meeting.participants || []
  },
  upload: {
    timestamp: new Date().toISOString(),
    source: body.source || 'api',
    fileType: hasFile ? 'audio' : (filePath ? 'webdav' : 'url'),
    fileExtension: fileExt
  },
  stt: {
    provider: 'deepgram',
    model: 'nova-2'
  },
  paths: paths
};

return {
  json: {
    isError: false,
    meeting,
    meta,
    audioUrl,
    filePath,
    hasFile,
    fileExt,
    paths
  },
  binary: binaryData
};

// Build STT payload; fall back to Extract & Validate Metadata to avoid data loss through WebDAV nodes
const baseNode = $node["Extract & Validate Metadata"];
const base = baseNode ? (Array.isArray(baseNode.json) ? baseNode.json[0] : baseNode.json) : {};

const meta    = $json.meta    || base.meta    || {};
const meeting = $json.meeting || base.meeting || {};
const paths   = $json.paths   || base.paths   || {};
const audioUrl = $json.audioUrl || base.audioUrl || null;
const filePath = $json.filePath || base.filePath || null; // WebDAV path from AUTO_TRIGGER
const hasFile  = $json.hasFile || false;

const binaryData = $binary || {};

const sttConfig   = meta.stt || {};
const sttProvider = sttConfig.provider || 'deepgram';
const sttModel    = sttConfig.model || 'nova-2';

return {
  json: {
    meeting,
    meta,
    audioUrl,
    filePath,
    hasFile,
    useBinaryMode: hasFile,
    useUrlMode: !hasFile && (!!audioUrl || !!filePath),
    sttProvider,
    sttModel,
    language: meeting.language || 'de',
    paths
  },
  binary: binaryData
};

const sttResponse = $json;

const extractNode = $node["Extract & Validate Metadata"];
const extractJson = extractNode ? extractNode.json : {};

const meeting = extractJson.meeting || {};
const basePaths = extractJson.paths || {};

const slug = meeting.slug || "undefined";
const base = basePaths.base || `/Meetings/${slug}`;

const paths = {
  base,
  raw: basePaths.raw || `${base}/raw`,
  out: basePaths.out || `${base}/out`,
  dlq: basePaths.dlq || `${base}/dlq`,
  originalFile: basePaths.originalFile || `${base}/raw/original.bin`,
  transcript: basePaths.transcript || `${base}/out/transcript.txt`,
  meta: basePaths.meta || `${base}/out/meta.json`,
};

const transcript = sttResponse.results.channels[0].alternatives[0].transcript;

const models = sttResponse.models || [];
const modelInfo = models[0] && models[0].model_info ? models[0].model_info : {};

const meta = {
  stt: {
    provider: "deepgram",
    model: modelInfo.name || "nova-2",
    requestId: sttResponse.metadata.request_id,
    duration: sttResponse.metadata.duration,
    sha256: sttResponse.metadata.sha256,
  },
  rawStt: sttResponse,
};

return {
  json: {
    isError: false,
    meeting,
    paths,
    transcript,
    transcriptText: transcript,
    meta,
    rawStt: sttResponse,
  },
};

// Prepare WF2 Data - Get data from Parse STT Response (STT path) or Load Existing Transcript (idempotency path)
const parseNode = $node['Parse STT Response'];
const loadNode = $node['Load Existing Transcript'];

let sourceData = null;

// Try Parse STT Response first (STT path)
if (parseNode && parseNode.json) {
  sourceData = Array.isArray(parseNode.json) ? parseNode.json[0] : parseNode.json;
}
// Fallback to Load Existing Transcript (idempotency path)
else if (loadNode && loadNode.json) {
  sourceData = Array.isArray(loadNode.json) ? loadNode.json[0] : loadNode.json;
}

if (!sourceData) {
  return {
    json: {
      meeting: {},
      meta: {},
      paths: {}
    }
  };
}

return {
  json: {
    meeting: sourceData.meeting || {},
    meta: sourceData.meta || {},
    paths: sourceData.paths || {}
  }
};

// Immer explizit von Parse STT Response lesen
const baseNode = $node['Parse STT Response'];

const meeting = baseNode.json.meeting || {};
const meta    = baseNode.json.meta    || {};
const paths   = baseNode.json.paths   || {};

const isCached = meta.transcript?.provider === 'cached';

return {
  json: {
    status: 'success',
    statusCode: 202,
    meeting: {
      slug: meeting.slug,
      title: meeting.title,
      datetime: meeting.datetime,
    },
    files: {
      transcript: paths.transcript || null,
      meta:       paths.meta       || null,
      original:   paths.originalFile || null,
    },
    transcript: {
      cached: !!isCached,
    },
  },
};

const errorData = $json;

// Use existing statusCode and error info if present (from validation errors)
// Otherwise default to 500
const statusCode = errorData.statusCode || 500;
const errorType = errorData.error || 'UNKNOWN_ERROR';
const errorMessage = errorData.message || 'Unknown error occurred';
const meeting = errorData.body?.meeting || errorData.meeting || { slug: 'unknown', title: 'Unknown' };

const dlqEntry = {
  timestamp: new Date().toISOString(),
  workflow: 'WF1-Ingest-v1.6.2',
  meetingSlug: meeting.slug,
  error: {
    type: errorType,
    message: errorMessage,
    statusCode: statusCode
  },
  request: {
    body: errorData.body,
    hasHeaders: !!errorData.headers
  }
};

return {
  json: {
    dlqEntry,
    statusCode: statusCode,
    errorType: errorType,
    errorMessage: errorMessage
  }
};

