const existingTranscript = $json.data;
const extractNode = $node["Extract & Validate Metadata"];
const extractJson = extractNode ? (Array.isArray(extractNode.json) ? extractNode.json[0] : extractNode.json) : {};
const meeting = extractJson.meeting || {};
const meta = extractJson.meta || {};
const paths = extractJson.paths || {};

return {
  json: {
    meeting,
    meta: {
      ...meta,
      transcript: {
        length: existingTranscript?.length || 0,
        confidence: 1.0,
        provider: 'cached',
        processedAt: new Date().toISOString(),
        note: 'Existing transcript loaded (idempotent)'
      }
    },
    transcriptText: existingTranscript || '',
    paths,
    skippedSTT: true
  }
};

// HMAC validation failed - build error response
const body = $json.body || {};
const headers = $json.headers || {};

return {
  json: {
    isError: true,
    error: 'HMAC_INVALID',
    message: 'Invalid HMAC signature (X-Signature-256 does not match calculated hash)',
    statusCode: 401,
    body,
    headers
  }
};

// Normalize Nonce Check Result and catch Nextcloud errors
const checkResult = $json;
const extractNode = $node["Extract Headers & Body"];
const extractData = extractNode ? (Array.isArray(extractNode.json) ? extractNode.json[0] : extractNode.json) : {};

// Helper to detect "file not found" cases without statusCode
const msg = (checkResult.message || checkResult.error || checkResult.body?.message || '').toString().toLowerCase();
const isNotFoundMsg = msg.includes('not exist') || msg.includes('not found') || msg.includes('не существует');

// 1) 404 → treat as not exists
if (checkResult.statusCode === 404 || isNotFoundMsg) {
  return {
    json: {
      ...extractData,
      exists: false,
      isError: false,
      nonceCheckFailed: false
    }
  };
}

// 2) 5xx or explicit error → cache unavailable
if (checkResult.error || (checkResult.statusCode && checkResult.statusCode >= 500)) {
  return {
    json: {
      isError: true,
      error: 'NONCE_CACHE_UNAVAILABLE',
      message: 'Nonce cache (Nextcloud) unavailable – cannot safely validate replay protection',
      statusCode: 503,
      body: extractData.body,
      headers: extractData.headers,
      nonceCheckFailed: true
    }
  };
}

// 3) Otherwise, normalize exists
const exists = checkResult.exists === true || checkResult.exists === 'true';

return {
  json: {
    ...extractData,
    exists: exists,
    isError: false,
    nonceCheckFailed: false
  }
};

// Nonce replay detected - build error response
const extractNode = $node["Extract Headers & Body"];
const extractData = extractNode ? (Array.isArray(extractNode.json) ? extractNode.json[0] : extractNode.json) : {};
const body = extractData.body || {};
const headers = extractData.headers || {};
const nonce = extractData.nonce || 'unknown';
const timestamp = extractData.timestamp || 'unknown';

return {
  json: {
    isError: true,
    error: 'REPLAY_DETECTED',
    message: `Replay detected: nonce has already been used for this timestamp (nonce: ${nonce}, timestamp: ${timestamp})`,
    statusCode: 401,
    body,
    headers
  }
};

const crypto = require('crypto');

// 1) Body wie echter Request
const body = {
  meeting: {
    slug: '2025/11/17 - KI B Test',
    title: 'Kickoff Testkunde',
    datetime: '2025-11-23T13:00:00Z',
    language: 'de',
    participants: ['Testperson']
  },
  // hier später deine echte Test-Audio-URL eintragen
  audioUrl: 'https://example.com/test-audio.wav',
  source: 'wf1-manual-test'
};

const rawBody = JSON.stringify(body);

// 2) Timestamp & Nonce
const timestamp = Math.floor(Date.now() / 1000);
const nonce = 'manual-test-' + timestamp;

// 3) HMAC berechnen (wenn HMAC_SECRET verfügbar)
const hmacSecret = $env.HMAC_SECRET;
let hmacSignature = 'sha256=dummy'; // Fallback für Tests ohne Secret

if (hmacSecret && hmacSecret !== '') {
  try {
    const hmac = crypto
      .createHmac('sha256', hmacSecret)
      .update(rawBody, 'utf8')
      .digest('hex');
    hmacSignature = `sha256=${hmac}`;
  } catch (error) {
    // Falls HMAC-Berechnung fehlschlägt, verwende Fallback
    console.warn('HMAC calculation failed, using dummy:', error.message);
  }
}

// 4) Header mit korrekter HMAC
const headers = {
  'x-signature-256': hmacSignature,
  'x-timestamp': String(timestamp),
  'x-nonce': nonce
};

// 5) Struktur wie beim Webhook-Input zurückgeben
return {
  json: {
    headers,
    body,
    rawBody
  }
};
